/// This class defines the interface for the tabulation of the cell
/// tensor corresponding to the local contribution to a form from
/// the integral over a cell.

class base_functions_cell_integral_1_otherwise: public ufc::cell_integral
{
public:

  /// Constructor
  base_functions_cell_integral_1_otherwise() : ufc::cell_integral()
  {
    // Do nothing
  }

  /// Destructor
  virtual ~base_functions_cell_integral_1_otherwise()
  {
    // Do nothing
  }

  /// Tabulate which form coefficients are used by this integral
  virtual const std::vector<bool> & enabled_coefficients() const
  {
    static const std::vector<bool> enabled({});
    return enabled;
  }

  /// Tabulate the tensor for the contribution from a local cell
  virtual void tabulate_tensor(double*  A,
                               const double * const *  w,
                               const double*  vertex_coordinates,
                               int cell_orientation) const
  {
    // Number of operations (multiply-add pairs) for Jacobian data:      3
    // Number of operations (multiply-add pairs) for geometry tensor:    0
    // Number of operations (multiply-add pairs) for tensor contraction: 6
    // Total number of operations (multiply-add pairs):                  9
    
    // Compute Jacobian
    double J[9];
    compute_jacobian_tetrahedron_3d(J, vertex_coordinates);
    
    // Compute Jacobian inverse and determinant
    double K[9];
    double detJ;
    compute_jacobian_inverse_tetrahedron_3d(K, detJ, J);
    
    // Set scale factor
    const double det = std::abs(detJ);
    
    // Compute geometry tensor
    const double G0_ = det;
    const double G1_ = det;
    const double G2_ = det;
    
    // Compute element tensor
    A[0] = 0.0416666666666667*G1_;
    A[1] = 0.0416666666666667*G1_;
    A[2] = 0.0416666666666667*G1_;
    A[3] = 0.0416666666666667*G1_;
    A[4] = 0.0416666666666667*G2_;
    A[5] = 0.0416666666666667*G2_;
    A[6] = 0.0416666666666667*G2_;
    A[7] = 0.0416666666666667*G2_;
    A[8] = 0.0416666666666667*G0_;
    A[9] = 0.0416666666666667*G0_;
    A[10] = 0.0416666666666667*G0_;
    A[11] = 0.0416666666666667*G0_;
  }

};

